import React,{ Component } from 'react';
import Media from 'react-bootstrap/Media'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import '../App.css';

const axios = require('axios');
class Movie extends Component{
        state={estandar:{},
               actore:["a","b","c","d","b","c","d","b","c","d","b","c","d","b","c","d","b","c","d"],
               escritore:["a","b","c","d","b","c","d","b","c","d","b","c","d","b","c","d","b","c","d"],
               directore:["a","b","c","d","b","c","d","b","c","d","b","c","d","b","c","d","b","c","d"],
                video:{}
        }
        
        async componentDidMount(){
            let number = this.props.pelicula.split("/")[1]
            const response = await axios.get("https://api.themoviedb.org/3/movie/"+number+"?api_key=466c19159235f9ce92fe9604a953aba2&language=es")
            this.setState(response);
            const response2 = await axios.get("https://api.themoviedb.org/3/movie/"+number+"/credits?api_key=466c19159235f9ce92fe9604a953aba2")
            let i=0;
            let actores=[];
            let escritores=[];
            let directores=[];
            for(i=0; i<3;i++){
                  actores.push(response2.cast[i])          
            }
            this.setState({actore:actores})
            response2.crew.forEach(element => {
                if(element.department=="Writing"){
                    escritores.push(element)
                }
               else if(element.department=="Directing"){
                    actores.push(element)
                }
    
            });
            this.setState({escritore:escritores})
            this.setState({actore:actores})

        }
        render () {
            return(
            <div>
                <Container fluid>
                    <Row className = "color2">
                        <Col className = "text">
                            <span className = "title">{this.state.estandar.data.title} </span><span className = "year">({this.state.estandar.data.release_date.split("-")[0]})</span>
                        </Col>
                        <Col>    
                            <img
                                id = "star"
                                height = "50px"
                                width = "50px"
                                className="mr-3"
                                src="https://upload.wikimedia.org/wikipedia/commons/4/44/Plain_Yellow_Star.png"
                                alt="Generic placeholder"
                            />                         
                        </Col>
                        <Col>
                            <span className = "rate">{this.state.estandar.data.vote_average}/10</span>
                            <br></br>
                            <span className = "cont">{this.state.estandar.data.vote_count}</span>
                        </Col>
                    </Row>
                    <Row>
                        <Col>
                            <img
                                id = "movie"
                                className="mr-3"
                                src={"https://image.tmdb.org/t/p/w500/"+this.state.estandar.data.poster_path}
                                alt="Generic placeholder"
                            />
                        </Col>
                        <Col>
                            <Row>
                                <Media.Body className = "text">
                                    <p className = "color3" id = "rcorners1" >
                                        {this.state.estandar.data.overview}           
                                    </p>
                                        <p align = "left"> 
                                            <strong> Director: </strong>  <br /> 
                                            <strong> Writers: </strong> Sam Mendes, Krysty Wilson-Cairns <br /> 
                                            <strong> Stars: </strong> Dean-Charles Chapman, George MacKay, Daniel Mays | See full cast & crew <br /> 
                                        </p>
                                </Media.Body>
                            </Row>
                            <Row>
                                <Col>
                                    <video id = "video" width="400" controls>
                                        <source src="https://www.youtube.com/watch?v=rZD6nMlC3tE" type="video/ogg"/>
                                        Your browser does not support HTML5 video.
                                    </video>
                                </Col>
                            </Row>
                        </Col>
                    </Row>
                    <Row>
                        
                    </Row>
                </Container>
            </div>
        )
        }
    };
export default Movie;
